/*Dag 2 deel 1*/Console.WriteLine(File.ReadAllLines("../../../input.txt").Select(line => line.Split()).Select(splitLine => new {TheirLetter = char.Parse(splitLine[0]), MyLetter = (char) (char.Parse(splitLine[1]) - 23) /*X->A,Y->B,Z->C*/}).Select(obj => obj.MyLetter - 64 + ((obj.TheirLetter - 64) % 3 + 1 == obj.MyLetter - 64 ? 6 /*Win*/ : obj.TheirLetter == obj.MyLetter ? 3 /*Draw*/ : 0 /*Loss*/)).Sum());
/*Dag 2 deel 2*/Console.WriteLine(File.ReadAllLines("../../../input.txt").Select(line => line.Split()).Select(splitLine => new {TheirLetter = char.Parse(splitLine[0]), MyLetter = char.Parse(splitLine[1])}).Select(stuff => stuff.MyLetter == 'Z' ? 6 + (stuff.TheirLetter - 64) % 3 + 1 /*Win*/ : stuff.MyLetter == 'Y' ? 3 + stuff.TheirLetter - 64 /*Draw*/ : stuff.TheirLetter % 3 + 1 /*Loss*/).Sum());